import React, { useState, useEffect, useContext } from 'react';
import { AppContext } from '../context/AppContext';
import { getResources, analyzeCareer } from '../services/api';

const Resources = () => {
  const { userSkills, userExpertise } = useContext(AppContext);
  const [loading, setLoading] = useState(true);
  const [loadingMore, setLoadingMore] = useState(false);
  const [error, setError] = useState(null);
  const [currentLimit, setCurrentLimit] = useState(5);
  const [selectedTopic, setSelectedTopic] = useState('all');
  const [allResources, setAllResources] = useState({ youtube_courses: [], articles: [] });
  const [displayedResources, setDisplayedResources] = useState({ youtube_courses: [], articles: [] });
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const [careerAnalysis, setCareerAnalysis] = useState(null);
  const [analysisLoading, setAnalysisLoading] = useState(true);
  const [analysisError, setAnalysisError] = useState(null);

  const topicOptions = [
    { value: 'all', label: 'All Topics', icon: '🎯' },
    { value: 'AI', label: 'Artificial Intelligence', icon: '🤖' },
    { value: 'Web Development', label: 'Web Development', icon: '🌐' },
    { value: 'Data Science', label: 'Data Science', icon: '📊' },
    { value: 'Mobile Development', label: 'Mobile Development', icon: '📱' },
    { value: 'DevOps', label: 'DevOps & Cloud', icon: '☁️' },
    { value: 'Cybersecurity', label: 'Cybersecurity', icon: '🔒' },
    { value: 'Design', label: 'UI/UX Design', icon: '🎨' },
    { value: 'Blockchain', label: 'Blockchain', icon: '⛓️' },
    { value: 'Game Development', label: 'Game Development', icon: '🎮' },
    { value: 'Machine Learning', label: 'Machine Learning', icon: '🧠' },
  ];

  const fetchCareerAnalysis = async () => {
    try {
      setAnalysisLoading(true);
      setAnalysisError(null);

      const analysis = await analyzeCareer(
        userSkills || 'General Technology',
        userExpertise || 'beginner'
      );
      
      setCareerAnalysis(analysis);
      console.log('Career analysis fetched:', analysis);
    } catch (err) {
      console.error('Error fetching career analysis:', err);
      setAnalysisError('Failed to load career analysis. Please try again.');
    } finally {
      setAnalysisLoading(false);
    }
  };

  const fetchResources = async (limit = 5, isLoadMore = false, topic = selectedTopic) => {
    try {
      if (isLoadMore) {
        setLoadingMore(true);
      } else {
        setLoading(true);
        setPage(1);
        setAllResources({ youtube_courses: [], articles: [] });
        setDisplayedResources({ youtube_courses: [], articles: [] });
      }
      setError(null);

      // Prepare request data with topic filter
      const requestData = {
        skills: userSkills || 'General Technology',
        expertise: userExpertise || 'beginner',
        limit: limit,
        topic: topic === 'all' ? null : topic
      };

      const response = await getResources(requestData);
      
      if (isLoadMore) {
        // Append new resources for pagination
        const newAllResources = {
          youtube_courses: [...allResources.youtube_courses, ...(response.youtube_courses || [])],
          articles: [...allResources.articles, ...(response.articles || [])]
        };
        setAllResources(newAllResources);
        
        // Update displayed resources with pagination
        const itemsPerPage = 6;
        const newPage = page + 1;
        const startIndex = (newPage - 1) * itemsPerPage;
        
        setDisplayedResources({
          youtube_courses: newAllResources.youtube_courses.slice(0, startIndex + itemsPerPage),
          articles: newAllResources.articles.slice(0, startIndex + itemsPerPage)
        });
        
        setPage(newPage);
        
        // Check if there are more items to load
        const totalItems = newAllResources.youtube_courses.length + newAllResources.articles.length;
        const displayedItems = startIndex + itemsPerPage;
        setHasMore(totalItems > displayedItems);
      } else {
        // Initial load or topic change
        const newResources = {
          youtube_courses: response.youtube_courses || [],
          articles: response.articles || []
        };
        setAllResources(newResources);
        
        // Show first page
        const itemsPerPage = 6;
        setDisplayedResources({
          youtube_courses: newResources.youtube_courses.slice(0, itemsPerPage),
          articles: newResources.articles.slice(0, itemsPerPage)
        });
        
        // Check if there are more items
        const totalItems = newResources.youtube_courses.length + newResources.articles.length;
        setHasMore(totalItems > itemsPerPage);
      }
      
      setCurrentLimit(limit);
    } catch (err) {
      console.error('Error fetching resources:', err);
      setError('Failed to load resources. Please try again.');
    } finally {
      setLoading(false);
      setLoadingMore(false);
    }
  };

  useEffect(() => {
    fetchResources();
    fetchCareerAnalysis();
  }, [userSkills, userExpertise]); // eslint-disable-line react-hooks/exhaustive-deps

  const handleTopicChange = (topic) => {
    setSelectedTopic(topic);
    fetchResources(currentLimit, false, topic);
    // Note: Career analysis doesn't need topic filter as it's based on user skills/expertise
  };

  const handleLoadMore = () => {
    if (hasMore && !loadingMore) {
      const newLimit = currentLimit + 5;
      fetchResources(newLimit, true);
    }
  };

  const ResourceCard = ({ title, url, type }) => (
    <div className="bg-white rounded-xl shadow-md hover:shadow-xl transition-all duration-300 p-6 border border-gray-100 hover:border-primary-200 transform hover:translate-y-1 hover:scale-105 group">
      <div className="flex flex-col h-full">
        <div className="flex-1">
          <div className="flex items-start justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-800 line-clamp-3 leading-tight group-hover:text-primary-700 transition-colors duration-300">
              {title}
            </h3>
            <div className={`ml-3 px-3 py-1 rounded-full text-xs font-medium shrink-0 ${
              type === 'youtube' 
                ? 'bg-red-100 text-red-700 group-hover:bg-red-200' 
                : 'bg-blue-100 text-blue-700 group-hover:bg-blue-200'
            } transition-colors duration-300`}>
              {type === 'youtube' ? 'Video' : 'Article'}
            </div>
          </div>
        </div>
        
        <div className="mt-4">
          <button
            onClick={() => window.open(url, '_blank', 'noopener,noreferrer')}
            className={`w-full py-3 px-4 rounded-lg font-medium transition-all duration-300 ${
              type === 'youtube'
                ? 'bg-red-600 hover:bg-red-700 text-white shadow-lg hover:shadow-red-200'
                : 'bg-blue-600 hover:bg-blue-700 text-white shadow-lg hover:shadow-blue-200'
            } transform hover:scale-105 group-hover:shadow-2xl`}
          >
            <div className="flex items-center justify-center space-x-2">
              {type === 'youtube' ? (
                <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                  <path d="M23.498 6.186a3.016 3.016 0 0 0-2.122-2.136C19.505 3.545 12 3.545 12 3.545s-7.505 0-9.377.505A3.017 3.017 0 0 0 .502 6.186C0 8.07 0 12 0 12s0 3.93.502 5.814a3.016 3.016 0 0 0 2.122 2.136c1.871.505 9.376.505 9.376.505s7.505 0 9.377-.505a3.015 3.015 0 0 0 2.122-2.136C24 15.93 24 12 24 12s0-3.93-.502-5.814zM9.545 15.568V8.432L15.818 12l-6.273 3.568z"/>
                </svg>
              ) : (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                </svg>
              )}
              <span>Open</span>
            </div>
          </button>
        </div>
      </div>
    </div>
  );

  const SkeletonCard = () => (
    <div className="bg-white rounded-xl shadow-md p-6 border border-gray-100 animate-pulse">
      <div className="flex items-start justify-between mb-4">
        <div className="flex-1">
          <div className="h-5 bg-gray-200 rounded mb-2"></div>
          <div className="h-4 bg-gray-200 rounded w-3/4"></div>
        </div>
        <div className="w-16 h-6 bg-gray-200 rounded-full ml-3"></div>
      </div>
      <div className="mt-4">
        <div className="w-full h-12 bg-gray-200 rounded-lg"></div>
      </div>
    </div>
  );

  const TopicFilter = () => (
    <div className="mb-8">
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div className="flex items-center space-x-3">
          <span className="text-lg font-semibold text-gray-700">Filter by Topic:</span>
          <div className="relative">
            <select
              value={selectedTopic}
              onChange={(e) => handleTopicChange(e.target.value)}
              className="appearance-none bg-white border border-gray-300 rounded-lg px-4 py-2 pr-8 text-gray-700 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 transition-colors duration-300 min-w-[200px]"
            >
              {topicOptions.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.icon} {option.label}
                </option>
              ))}
            </select>
            <div className="absolute inset-y-0 right-0 flex items-center px-2 pointer-events-none">
              <svg className="w-4 h-4 text-gray-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
              </svg>
            </div>
          </div>
        </div>
        
        <div className="flex items-center space-x-2 text-sm text-gray-600">
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
          <span>Showing results for: <strong>{topicOptions.find(opt => opt.value === selectedTopic)?.label}</strong></span>
        </div>
      </div>
    </div>
  );

  const EmptyState = () => (
    <div className="text-center py-16">
      <div className="max-w-md mx-auto">
        <div className="w-24 h-24 bg-gradient-to-br from-gray-100 to-gray-200 rounded-full flex items-center justify-center mx-auto mb-6">
          <svg className="w-12 h-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
          </svg>
        </div>
        <h3 className="text-xl font-semibold text-gray-900 mb-2">No resources yet</h3>
        <p className="text-gray-600 mb-6">Try updating your skills to get personalized learning recommendations.</p>
        <button
          onClick={() => {
            fetchResources();
            fetchCareerAnalysis();
          }}
          className="inline-flex items-center px-6 py-3 bg-primary-600 text-white font-medium rounded-lg hover:bg-primary-700 transition-colors duration-300 shadow-lg hover:shadow-xl"
        >
          <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
          </svg>
          Refresh Resources
        </button>
      </div>
    </div>
  );

  // Career Analysis Components
  const CareerPathCard = ({ path, isSelected = false }) => (
    <div className={`bg-white rounded-xl shadow-md hover:shadow-xl transition-all duration-300 p-6 border ${
      isSelected ? 'border-primary-500 ring-2 ring-primary-200' : 'border-gray-100 hover:border-primary-200'
    } transform hover:translate-y-1 hover:scale-105`}>
      <div className="flex flex-col h-full">
        {isSelected && (
          <div className="flex items-center mb-3">
            <span className="bg-primary-100 text-primary-700 px-3 py-1 rounded-full text-sm font-medium flex items-center">
              <svg className="w-4 h-4 mr-1" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
              </svg>
              Recommended
            </span>
          </div>
        )}
        <h3 className="text-xl font-bold text-gray-900 mb-3">{path.title}</h3>
        <p className="text-gray-600 mb-4 flex-1">{path.description}</p>
        
        <div className="space-y-3">
          <div>
            <span className="text-sm font-medium text-gray-700">💰 Salary Range:</span>
            <p className="text-primary-600 font-semibold">{path.salary_range}</p>
          </div>
          
          <div>
            <span className="text-sm font-medium text-gray-700">📈 Growth Prospect:</span>
            <p className="text-gray-600">{path.growth_prospect}</p>
          </div>
          
          <div>
            <span className="text-sm font-medium text-gray-700">🛠️ Required Skills:</span>
            <div className="flex flex-wrap gap-2 mt-2">
              {path.required_skills?.map((skill, index) => (
                <span key={index} className="bg-gray-100 text-gray-700 px-2 py-1 rounded text-sm">
                  {skill}
                </span>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const RoadmapStep = ({ step, index, totalSteps }) => (
    <div className="flex items-start space-x-4">
      <div className="flex flex-col items-center">
        <div className={`w-10 h-10 rounded-full flex items-center justify-center font-bold text-white ${
          index < totalSteps - 1 ? 'bg-primary-600' : 'bg-green-600'
        }`}>
          {step.step}
        </div>
        {index < totalSteps - 1 && (
          <div className="w-0.5 h-16 bg-gradient-to-b from-primary-300 to-gray-200 mt-2"></div>
        )}
      </div>
      
      <div className="flex-1 pb-8">
        <div className="bg-white rounded-xl shadow-md p-6 border border-gray-100 hover:shadow-lg transition-shadow duration-300">
          <h4 className="text-lg font-semibold text-gray-900 mb-2">{step.title}</h4>
          <p className="text-gray-600 mb-3">{step.description}</p>
          
          <div className="flex items-center text-sm text-primary-600 mb-3">
            <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <span className="font-medium">{step.duration}</span>
          </div>
          
          {step.resources && step.resources.length > 0 && (
            <div>
              <span className="text-sm font-medium text-gray-700">📚 Resources:</span>
              <ul className="mt-2 space-y-1">
                {step.resources.map((resource, idx) => (
                  <li key={idx} className="text-sm text-gray-600 flex items-start">
                    <span className="text-primary-500 mr-2">•</span>
                    {resource}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  const CourseCard = ({ course }) => (
    <div className="bg-white rounded-xl shadow-md hover:shadow-xl transition-all duration-300 p-6 border border-gray-100 hover:border-primary-200 transform hover:translate-y-1 hover:scale-105">
      <div className="flex flex-col h-full">
        <h4 className="text-lg font-semibold text-gray-900 mb-2">{course.title}</h4>
        
        <div className="space-y-2 mb-4 flex-1">
          <div className="flex items-center text-sm">
            <span className="text-gray-500 mr-2">🏢</span>
            <span className="text-gray-700">{course.provider}</span>
          </div>
          
          <div className="flex items-center text-sm">
            <span className="text-gray-500 mr-2">⏱️</span>
            <span className="text-gray-700">{course.duration}</span>
          </div>
          
          <div className="flex items-center text-sm">
            <span className="text-gray-500 mr-2">📊</span>
            <span className={`px-2 py-1 rounded-full text-xs font-medium ${
              course.difficulty === 'Beginner' ? 'bg-green-100 text-green-700' :
              course.difficulty === 'Intermediate' ? 'bg-yellow-100 text-yellow-700' :
              'bg-red-100 text-red-700'
            }`}>
              {course.difficulty}
            </span>
          </div>
        </div>
        
        <button
          onClick={() => window.open(course.url, '_blank', 'noopener,noreferrer')}
          className="w-full py-3 px-4 bg-gradient-to-r from-primary-600 to-primary-700 text-white font-medium rounded-lg hover:from-primary-700 hover:to-primary-800 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:scale-105"
        >
          <div className="flex items-center justify-center space-x-2">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
            </svg>
            <span>View Course</span>
          </div>
        </button>
      </div>
    </div>
  );

  if (loading || analysisLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {/* Header Skeleton */}
          <div className="text-center mb-12">
            <div className="h-10 bg-gray-200 rounded mx-auto w-80 mb-4 animate-pulse"></div>
            <div className="h-6 bg-gray-200 rounded mx-auto w-96 animate-pulse"></div>
          </div>

          {/* Content Skeleton */}
          <div className="space-y-12">
            {/* YouTube Courses Section */}
            <div>
              <div className="flex items-center mb-6">
                <div className="w-8 h-8 bg-red-100 rounded-lg animate-pulse mr-3"></div>
                <div className="h-8 bg-gray-200 rounded w-48 animate-pulse"></div>
                <div className="ml-auto w-20 h-6 bg-gray-200 rounded-full animate-pulse"></div>
              </div>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {[...Array(3)].map((_, index) => (
                  <SkeletonCard key={`youtube-skeleton-${index}`} />
                ))}
              </div>
            </div>

            {/* Articles Section */}
            <div>
              <div className="flex items-center mb-6">
                <div className="w-8 h-8 bg-blue-100 rounded-lg animate-pulse mr-3"></div>
                <div className="h-8 bg-gray-200 rounded w-40 animate-pulse"></div>
                <div className="ml-auto w-20 h-6 bg-gray-200 rounded-full animate-pulse"></div>
              </div>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {[...Array(3)].map((_, index) => (
                  <SkeletonCard key={`article-skeleton-${index}`} />
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error || analysisError) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <div className="bg-red-50 border border-red-200 rounded-xl p-8 max-w-md mx-auto">
              <div className="flex items-center justify-center w-16 h-16 bg-red-100 rounded-full mx-auto mb-4">
                <svg className="w-8 h-8 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.982 16.5c-.77.833.192 2.5 1.732 2.5z" />
                </svg>
              </div>
              <h3 className="text-lg font-semibold text-red-800 mb-2">
                {error ? 'Error Loading Resources' : 'Error Loading Career Analysis'}
              </h3>
              <p className="text-red-600 mb-4">{error || analysisError}</p>
              <button
                onClick={() => {
                  if (error) fetchResources();
                  if (analysisError) fetchCareerAnalysis();
                }}
                className="px-6 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors duration-300"
              >
                Try Again
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  const hasResources = displayedResources?.youtube_courses?.length > 0 || displayedResources?.articles?.length > 0;

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            🎯 Career Analysis & Learning Resources
          </h1>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto">
            Complete career guidance and curated learning materials for: <span className="font-semibold text-primary-600">{userSkills || 'General Technology'}</span> at <span className="font-semibold text-primary-600">{userExpertise || 'beginner'}</span> level
          </p>
        </div>

        {/* Topic Filter */}
        {!loading && !analysisLoading && <TopicFilter />}

        {/* Career Analysis Section */}
        {careerAnalysis && !analysisLoading && (
          <div className="space-y-12 mb-16">
            {/* Recommended Career Path */}
            {careerAnalysis.selected_path && (
              <section>
                <div className="flex items-center mb-6">
                  <div className="flex items-center space-x-3">
                    <div className="w-8 h-8 bg-primary-100 rounded-lg flex items-center justify-center">
                      <svg className="w-5 h-5 text-primary-600" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                      </svg>
                    </div>
                    <h2 className="text-2xl font-bold text-gray-900">🎯 Recommended Career Path</h2>
                  </div>
                </div>
                
                <div className="grid grid-cols-1 gap-6">
                  <CareerPathCard path={careerAnalysis.selected_path} isSelected={true} />
                </div>
              </section>
            )}

            {/* All Career Paths */}
            {careerAnalysis.career_paths && careerAnalysis.career_paths.length > 0 && (
              <section>
                <div className="flex items-center mb-6">
                  <div className="flex items-center space-x-3">
                    <div className="w-8 h-8 bg-purple-100 rounded-lg flex items-center justify-center">
                      <svg className="w-5 h-5 text-purple-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4M7.835 4.697a3.42 3.42 0 001.946-.806 3.42 3.42 0 014.438 0 3.42 3.42 0 001.946.806 3.42 3.42 0 013.138 3.138 3.42 3.42 0 00.806 1.946 3.42 3.42 0 010 4.438 3.42 3.42 0 00-.806 1.946 3.42 3.42 0 01-3.138 3.138 3.42 3.42 0 00-1.946.806 3.42 3.42 0 01-4.438 0 3.42 3.42 0 00-1.946-.806 3.42 3.42 0 01-3.138-3.138 3.42 3.42 0 00-.806-1.946 3.42 3.42 0 010-4.438 3.42 3.42 0 00.806-1.946 3.42 3.42 0 013.138-3.138z" />
                      </svg>
                    </div>
                    <h2 className="text-2xl font-bold text-gray-900">🚀 All Career Paths</h2>
                  </div>
                  <div className="ml-auto">
                    <span className="bg-purple-100 text-purple-700 px-3 py-1 rounded-full text-sm font-medium">
                      {careerAnalysis.career_paths.length} paths
                    </span>
                  </div>
                </div>
                
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {careerAnalysis.career_paths.map((path, index) => (
                    <CareerPathCard 
                      key={`path-${index}`} 
                      path={path} 
                      isSelected={path.title === careerAnalysis.selected_path?.title}
                    />
                  ))}
                </div>
              </section>
            )}

            {/* Career Roadmap */}
            {careerAnalysis.roadmap && careerAnalysis.roadmap.length > 0 && (
              <section>
                <div className="flex items-center mb-6">
                  <div className="flex items-center space-x-3">
                    <div className="w-8 h-8 bg-green-100 rounded-lg flex items-center justify-center">
                      <svg className="w-5 h-5 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 20l-5.447-2.724A1 1 0 013 16.382V5.618a1 1 0 011.447-.894L9 7m0 13l6-3m-6 3V7m6 10l4.553 2.276A1 1 0 0021 18.382V7.618a1 1 0 00-.553-.894L15 4m0 13V4m0 0L9 7" />
                      </svg>
                    </div>
                    <h2 className="text-2xl font-bold text-gray-900">🗺️ Career Roadmap</h2>
                  </div>
                  <div className="ml-auto">
                    <span className="bg-green-100 text-green-700 px-3 py-1 rounded-full text-sm font-medium">
                      {careerAnalysis.roadmap.length} steps
                    </span>
                  </div>
                </div>
                
                <div className="bg-white rounded-xl shadow-lg p-8 border border-gray-100">
                  <div className="space-y-4">
                    {careerAnalysis.roadmap.map((step, index) => (
                      <RoadmapStep 
                        key={`step-${index}`} 
                        step={step} 
                        index={index} 
                        totalSteps={careerAnalysis.roadmap.length}
                      />
                    ))}
                  </div>
                </div>
              </section>
            )}

            {/* Recommended Courses */}
            {careerAnalysis.courses && careerAnalysis.courses.length > 0 && (
              <section>
                <div className="flex items-center mb-6">
                  <div className="flex items-center space-x-3">
                    <div className="w-8 h-8 bg-orange-100 rounded-lg flex items-center justify-center">
                      <svg className="w-5 h-5 text-orange-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
                      </svg>
                    </div>
                    <h2 className="text-2xl font-bold text-gray-900">📖 Recommended Courses</h2>
                  </div>
                  <div className="ml-auto">
                    <span className="bg-orange-100 text-orange-700 px-3 py-1 rounded-full text-sm font-medium">
                      {careerAnalysis.courses.length} courses
                    </span>
                  </div>
                </div>
                
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {careerAnalysis.courses.map((course, index) => (
                    <CourseCard key={`course-${index}`} course={course} />
                  ))}
                </div>
              </section>
            )}
          </div>
        )}

        {/* Learning Resources Section Divider */}
        {careerAnalysis && !analysisLoading && hasResources && (
          <div className="border-t border-gray-200 my-12 pt-12">
            <div className="text-center mb-8">
              <h2 className="text-3xl font-bold text-gray-900 mb-2">📚 Additional Learning Resources</h2>
              <p className="text-gray-600">Supplementary videos and articles to enhance your learning</p>
            </div>
          </div>
        )}

        {!hasResources ? (
          <EmptyState />
        ) : (
          <div className="space-y-12">
            {/* YouTube Courses Section */}
            {displayedResources?.youtube_courses && displayedResources.youtube_courses.length > 0 && (
              <section>
                <div className="flex items-center mb-6">
                  <div className="flex items-center space-x-3">
                    <div className="w-8 h-8 bg-red-100 rounded-lg flex items-center justify-center">
                      <svg className="w-5 h-5 text-red-600" fill="currentColor" viewBox="0 0 24 24">
                        <path d="M23.498 6.186a3.016 3.016 0 0 0-2.122-2.136C19.505 3.545 12 3.545 12 3.545s-7.505 0-9.377.505A3.017 3.017 0 0 0 .502 6.186C0 8.07 0 12 0 12s0 3.93.502 5.814a3.016 3.016 0 0 0 2.122 2.136c1.871.505 9.376.505 9.376.505s7.505 0 9.377-.505a3.015 3.015 0 0 0 2.122-2.136C24 15.93 24 12 24 12s0-3.93-.502-5.814zM9.545 15.568V8.432L15.818 12l-6.273 3.568z"/>
                      </svg>
                    </div>
                    <h2 className="text-2xl font-bold text-gray-900">YouTube Courses</h2>
                  </div>
                  <div className="ml-auto">
                    <span className="bg-red-100 text-red-700 px-3 py-1 rounded-full text-sm font-medium">
                      {displayedResources.youtube_courses.length} of {allResources.youtube_courses.length} courses
                    </span>
                  </div>
                </div>
                
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {displayedResources.youtube_courses.map((course, index) => (
                    <ResourceCard
                      key={`youtube-${index}`}
                      title={course.title}
                      url={course.url}
                      type="youtube"
                    />
                  ))}
                </div>
              </section>
            )}

            {/* Articles Section */}
            {displayedResources?.articles && displayedResources.articles.length > 0 && (
              <section>
                <div className="flex items-center mb-6">
                  <div className="flex items-center space-x-3">
                    <div className="w-8 h-8 bg-blue-100 rounded-lg flex items-center justify-center">
                      <svg className="w-5 h-5 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                      </svg>
                    </div>
                    <h2 className="text-2xl font-bold text-gray-900">Articles & Guides</h2>
                  </div>
                  <div className="ml-auto">
                    <span className="bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-sm font-medium">
                      {displayedResources.articles.length} of {allResources.articles.length} articles
                    </span>
                  </div>
                </div>
                
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {displayedResources.articles.map((article, index) => (
                    <ResourceCard
                      key={`article-${index}`}
                      title={article.title}
                      url={article.url}
                      type="article"
                    />
                  ))}
                </div>
              </section>
            )}

            {/* Load More Button */}
            {hasMore && (
              <div className="text-center mt-12">
                <button
                  onClick={handleLoadMore}
                  disabled={loadingMore}
                  className="inline-flex items-center space-x-3 px-8 py-4 bg-gradient-to-r from-primary-600 to-primary-700 text-white font-semibold rounded-xl shadow-lg hover:shadow-xl transform hover:scale-105 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
                >
                  {loadingMore ? (
                    <>
                      <svg className="animate-spin w-5 h-5" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                      </svg>
                      <span>Loading More...</span>
                    </>
                  ) : (
                    <>
                      <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                      </svg>
                      <span>Load More Resources</span>
                      <span className="bg-white bg-opacity-20 px-2 py-1 rounded text-sm">Page {page + 1}</span>
                    </>
                  )}
                </button>
              </div>
            )}

            {/* No More Results */}
            {!hasMore && hasResources && (
              <div className="text-center mt-12 py-8">
                <div className="inline-flex items-center space-x-2 text-gray-600">
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                  <span className="font-medium">You've seen all available resources for this topic</span>
                </div>
                <p className="text-sm text-gray-500 mt-2">
                  Try changing the topic filter to discover more learning materials
                </p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Resources;
